import React from 'react';

import {Route } from 'react-router-dom';
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';

import CollectionOverview from '../../components/collections-overview/collections-overview.component';
import CollectionPage from '../collection/collection.component';

import { fetchCollectionsStartAsync } from '../../redux/shop/shop.actions';
import { selectIsCollectionFetching, selectIsCollectionsLoaded } from '../../redux/shop/shop.selectors';

import WithSpinner from '../../components/with-spinner/with-spinner.component';

const CollectionOverviewWithSpinner = WithSpinner(CollectionOverview);
const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends React.Component {

  componentDidMount() {
    const { fetchCollectionsStartAsync } = this.props;
    fetchCollectionsStartAsync();
  }

  render() {
    const { match, isCollectionFetching, isCollectionLoading } = this.props;
    
    return(
      <div className='shop-page'> 
        {/* <Route exact path={`${match.path}`} component={CollectionOverview} /> */}
        <Route 
          exact 
          path={`${match.path}`} 
          render={(props) => (
            <CollectionOverviewWithSpinner isLoading={isCollectionFetching} {...props} />
            )} 
        />
        <Route 
          path={`${match.path}/:categoryId`} 
          render={(props) => (
            <CollectionPageWithSpinner isLoading={!isCollectionsLoading} {...props} />
          )} 
        />
        {/* <Route path={`${match.path}/:categoryId`} component={CollectionPage} /> */}
      </div>
    );
  }
} 

const mapToStateProps = createStructuredSelector({
  isCollectionFetching : selectIsCollectionFetching,
  isCollectionsLoading : selectIsCollectionsLoaded
});

const mapDispatchToProps = dispatch => ({
  fetchCollectionsStartAsync : () => dispatch(fetchCollectionsStartAsync())
});

export default connect(mapToStateProps, mapDispatchToProps)(ShopPage);
